import React, { useState, useEffect, useCallback } from 'react';
import { ThemeProvider, createTheme, styled } from '@mui/material/styles';
import { Button, Typography, Box, Container, LinearProgress } from '@mui/material';
import { startGame, getQuestion, submitAnswer } from '../api';
import { QuestionData, AnswerResponse } from '../types';
import Question from './Question';
import Result from './Result';

// Create audio objects for each sound
const correctSound = new Audio('/sounds/correct.mp3');
const incorrectSound = new Audio('/sounds/incorrect.mp3');
const gameOverSound = new Audio('/sounds/game-over.mp3');

// Define a custom theme using Material-UI
const theme = createTheme({
  palette: {
    primary: {
      main: '#1E3D58', // Deep blue for primary elements
    },
    secondary: {
      main: '#FFC300', // Soft yellow for buttons and highlights
    },
    success: {
      main: '#43B929', // Green for correct answers
    },
    background: {
      default: '#1E3D58', // Background color for the app
      paper: '#FFFFFF', // Background color for cards
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 700,
    },
    h6: {
      fontFamily: '"Montserrat", sans-serif',
      fontWeight: 600,
    },
  },
});

// Styled container for the main content
const StyledContainer = styled(Container)(({ theme }) => ({
  backgroundColor: theme.palette.background.paper,
  borderRadius: theme.shape.borderRadius,
  padding: theme.spacing(3),
  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
}));

const Game: React.FC = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [questionData, setQuestionData] = useState<QuestionData | null>(null);
  const [result, setResult] = useState<AnswerResponse | null>(null);
  const [score, setScore] = useState(0);
  const [totalRounds, setTotalRounds] = useState(5);
  const [highScore, setHighScore] = useState(0);
  const [timer, setTimer] = useState(10);
  const [intervalId, setIntervalId] = useState<NodeJS.Timeout | null>(null);

  useEffect(() => {
    const storedHighScore = localStorage.getItem('highScore');
    if (storedHighScore) {
      setHighScore(parseInt(storedHighScore, 10));
    }
  }, []);

  const playSound = useCallback((sound: HTMLAudioElement) => {
    sound.currentTime = 0;
    sound.play();
  }, []);

  const startTimer = useCallback(() => {
    setTimer(10);
    const id = setInterval(() => {
      setTimer((prevTimer) => {
        if (prevTimer <= 1) {
          clearInterval(id);
          handleAnswer(''); // Submit empty answer when time's up
          return 0;
        }
        return prevTimer - 1;
      });
    }, 1000);
    setIntervalId(id);
  }, []);

  const stopTimer = useCallback(() => {
    if (intervalId) {
      clearInterval(intervalId);
    }
  }, [intervalId]);

  const handleStartGame = async () => {
    const { totalRounds } = await startGame();
    setTotalRounds(totalRounds);
    setGameStarted(true);
    setScore(0);
    fetchQuestion();
  };

  const fetchQuestion = async () => {
    const data = await getQuestion();
    setQuestionData(data);
    setResult(null);
    startTimer();
  };

  const calculatePoints = (timeLeft: number): number => {
    return Math.round((timeLeft / 10) * 1000);
  };

  const handleAnswer = async (answer: string) => {
    stopTimer();
    const points = calculatePoints(timer);
    const response = await submitAnswer(answer);

    let newScore = score;
    if (response.isCorrect) {
      newScore += points;
      playSound(correctSound);
    } else {
      playSound(incorrectSound);
    }

    setScore(newScore);
    setResult({ ...response, points });

    if (response.gameOver) {
      playSound(gameOverSound);
      if (newScore > highScore) {
        setHighScore(newScore);
        localStorage.setItem('highScore', newScore.toString());
      }
    } else {
      setTimeout(fetchQuestion, 2000);
    }
  };

  const handlePlayAgain = () => {
    setGameStarted(false);
    setQuestionData(null);
    setResult(null);
    setScore(0);
  };

  return (
    <ThemeProvider theme={theme}>
      <Box 
        sx={{ 
          minHeight: '100vh', 
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundImage: 'url("/images/world-map-outline.svg")',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
        }}
      >
        <StyledContainer maxWidth="sm">
          <Typography variant="h4" gutterBottom align="center" color="primary">
            Geography Trivia
          </Typography>
          {gameStarted && (
            <>
              <LinearProgress 
                variant="determinate" 
                value={(score / (totalRounds * 1000)) * 100} 
                sx={{ mb: 2 }}
              />
              <Typography variant="h6" align="center" gutterBottom>
                Time left: {timer}s
              </Typography>
            </>
          )}
          {!gameStarted ? (
            <Box textAlign="center">
              <Typography variant="h6" gutterBottom>
                High Score: {highScore}
              </Typography>
              <Button 
                variant="contained" 
                color="secondary" 
                onClick={handleStartGame}
                size="large"
              >
                Start Game
              </Button>
            </Box>
          ) : (
            <>
              <Typography variant="h6" gutterBottom align="center">
                Score: {score} / {totalRounds * 1000}
              </Typography>
              {questionData && !result && (
                <Question data={questionData} onAnswer={handleAnswer} />
              )}
              {result && (
                <Result
                  result={result}
                  score={score}
                  totalRounds={totalRounds}
                  highScore={highScore}
                  onPlayAgain={handlePlayAgain}
                />
              )}
            </>
          )}
        </StyledContainer>
      </Box>
    </ThemeProvider>
  );
};

export default Game;
