import React, { useState, useEffect } from 'react';
import { Button, Typography, Box, Container } from '@mui/material';
import { startGame, getQuestion, submitAnswer } from '../api';
import { QuestionData, AnswerResponse } from '../types';
import Question from './Question';
import Result from './Result';

const Game: React.FC = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [questionData, setQuestionData] = useState<QuestionData | null>(null);
  const [result, setResult] = useState<AnswerResponse | null>(null);
  const [score, setScore] = useState(0);
  const [totalRounds, setTotalRounds] = useState(5);

  const handleStartGame = async () => {
    const { totalRounds } = await startGame();
    setTotalRounds(totalRounds);
    setGameStarted(true);
    setScore(0);
    fetchQuestion();
  };

  const fetchQuestion = async () => {
    const data = await getQuestion();
    setQuestionData(data);
    setResult(null);
  };

  const handleAnswer = async (answer: string) => {
    const response = await submitAnswer(answer);
    setResult(response);
    setScore(response.score);

    if (!response.gameOver) {
      setTimeout(fetchQuestion, 2000);
    }
  };

  const handlePlayAgain = () => {
    setGameStarted(false);
    setQuestionData(null);
    setResult(null);
  };

  useEffect(() => {
    if (gameStarted && !questionData && !result) {
      fetchQuestion();
    }
  }, [gameStarted, questionData, result]);

  if (!gameStarted) {
    return (
      <Container maxWidth="sm">
        <Box textAlign="center" mt={4}>
          <Typography variant="h4" gutterBottom>
            Geography Trivia Game
          </Typography>
          <Button variant="contained" color="primary" onClick={handleStartGame}>
            Start Game
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="sm">
      <Box textAlign="center" mt={4}>
        <Typography variant="h4" gutterBottom>
          Geography Trivia Game
        </Typography>
        <Typography variant="h6" gutterBottom>
          Score: {score} / {totalRounds}
        </Typography>
        {questionData && !result && (
          <Question data={questionData} onAnswer={handleAnswer} />
        )}
        {result && (
          <Result
            result={result}
            onNextQuestion={fetchQuestion}
            onPlayAgain={handlePlayAgain}
          />
        )}
      </Box>
    </Container>
  );
};

export default Game;
