import React, { useState, useEffect, useCallback } from 'react';
import { ThemeProvider, createTheme, styled } from '@mui/material/styles';
import { Button, Typography, Box, Container } from '@mui/material';
import { startGame, getQuestion, submitAnswer } from '../api';
import { QuestionData, AnswerResponse } from '../types';
import Question from './Question';
import Result from './Result';

// Create audio objects for each sound
const correctSound = new Audio('/sounds/correct.mp3');
const incorrectSound = new Audio('/sounds/incorrect.mp3');
const gameOverSound = new Audio('/sounds/game-over.mp3');

// Define a custom theme using Material-UI
const theme = createTheme({
  palette: {
    primary: {
      main: '#46178f', // Kahoot-like purple for primary elements
    },
    secondary: {
      main: '#FFC300', // Soft yellow for buttons and highlights
    },
    success: {
      main: '#43B929', // Green for correct answers
    },
    background: {
      default: '#A500B5', // Background color for the app
      paper: '#FFFFFF', // Background color for cards
    },
  },
  typography: {
    fontFamily: '"Montserrat", "Roboto"',
    h4: {
      fontWeight: 700,
    },
    h6: {
      fontWeight: 600,
    },
  },
});

// Styled container for the main content
const StyledContainer = styled(Container)(({ theme }) => ({
  backgroundColor: theme.palette.background.paper,
  borderRadius: theme.shape.borderRadius,
  padding: theme.spacing(3),
  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
}));

const Game: React.FC = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [questionData, setQuestionData] = useState<QuestionData | null>(null);
  const [result, setResult] = useState<AnswerResponse | null>(null);
  const [score, setScore] = useState(0);
  const [totalRounds, setTotalRounds] = useState(5);
  const [highScore, setHighScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(10);
  const totalTime = 10; // Total time for each question

  useEffect(() => {
    const storedHighScore = localStorage.getItem('highScore');
    if (storedHighScore) {
      setHighScore(parseInt(storedHighScore, 10));
    }
  }, []);

  useEffect(() => {
    let timer: NodeJS.Timeout;
    if (gameStarted && questionData && !result) {
      timer = setInterval(() => {
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(timer);
            handleAnswer(''); // Submit empty answer when time's up
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [gameStarted, questionData, result]);

  const playSound = useCallback((sound: HTMLAudioElement) => {
    sound.currentTime = 0;
    sound.play();
  }, []);

  const handleStartGame = async () => {
    const { totalRounds } = await startGame();
    setTotalRounds(totalRounds);
    setGameStarted(true);
    setScore(0);
    fetchQuestion();
  };

  const fetchQuestion = async () => {
    const data = await getQuestion();
    setQuestionData(data);
    setResult(null);
    setTimeLeft(totalTime);
  };

  const calculatePoints = (timeLeft: number): number => {
    return Math.round((timeLeft / totalTime) * 1000);
  };

  const handleAnswer = async (answer: string) => {
    const points = calculatePoints(timeLeft);
    const response = await submitAnswer(answer);

    let earnedPoints = 0;
    if (response.isCorrect) {
      earnedPoints = points;
      setScore((prevScore) => prevScore + points);
      playSound(correctSound);
    } else {
      playSound(incorrectSound);
    }

    setResult({ ...response, points: earnedPoints });

    if (response.gameOver) {
      playSound(gameOverSound);
      const finalScore = score + earnedPoints;
      if (finalScore > highScore) {
        setHighScore(finalScore);
        localStorage.setItem('highScore', finalScore.toString());
      }
    } else {
      setTimeout(fetchQuestion, 2000);
    }
  };

  const handlePlayAgain = () => {
    setGameStarted(false);
    setQuestionData(null);
    setResult(null);
    setScore(0);
  };

  return (
    <ThemeProvider theme={theme}>
      <Box 
        sx={{ 
          minHeight: '100vh', 
          backgroundColor: 'primary.main',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundImage: 'url("/images/world-map-outline.svg")',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
        }}
      >
        <StyledContainer maxWidth="sm">
          <Typography variant="h4" gutterBottom align="center" color="primary">
            Geography Trivia
          </Typography>
          {!gameStarted ? (
            <Box textAlign="center">
              <Typography variant="h6" gutterBottom>
                High Score: {highScore}
              </Typography>
              <Button 
                variant="contained" 
                color="secondary" 
                onClick={handleStartGame}
                size="large"
              >
                Start Game
              </Button>
            </Box>
          ) : (
            <>
              <Typography variant="h6" gutterBottom align="center">
                Score: {score}
              </Typography>
              {questionData && !result && (
                <Question 
                  data={questionData} 
                  onAnswer={handleAnswer}
                  timeLeft={timeLeft}
                  totalTime={totalTime}
                />
              )}
              {result && (
                <Result
                  result={result}
                  score={score}
                  totalRounds={totalRounds}
                  highScore={highScore}
                  onPlayAgain={handlePlayAgain}
                />
              )}
            </>
          )}
        </StyledContainer>
      </Box>
    </ThemeProvider>
  );
};

export default Game;
